CREATE FUNCTION F_FDM.Next_Rprc_Day(etl_date Date, start_date Date, end_date Date, change_month Varchar)
RETURN date AS
BEGIN
RETURN CASE WHEN (add_months(start_date, ((ceil(("datediff"('month', start_date, etl_date) / change_month)) * (change_month)::int))::int) < end_date) THEN add_months(start_date, ((ceil(("datediff"('month', start_date, etl_date) / change_month)) * (change_month)::int))::int) ELSE end_date END;
END;

CREATE FUNCTION F_FDM.max_month(etl_date Date)
RETURN varchar AS
BEGIN
RETURN CASE WHEN (date_part('quarter', etl_date) = 1) THEN '03' WHEN (date_part('quarter', etl_date) = 2) THEN '06' WHEN (date_part('quarter', etl_date) = 3) THEN '09' WHEN (date_part('quarter', etl_date) = 4) THEN '12' ELSE '0' END;
END;

